import React from 'react';

import { Table, Divider, Modal, Button } from 'antd';
import Booking from './booking-form';
import MainLayout from './main-layout';

//Modal Code 

class AddBooking extends React.Component {
  state = {
    visible: false,
    confirmLoading: false,
  };

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = () => {
    this.setState({
      ModalText: 'The modal will be closed after two seconds',
      confirmLoading: true,
    });
    setTimeout(() => {
      this.setState({
        visible: false,
        confirmLoading: false,
      });
    }, 2000);
  };

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  };

  render() {
    const { visible, confirmLoading } = this.state;
    return (
      <div>
        <Button type="primary" onClick={this.showModal}>
            Add Booking
        </Button>
        <Modal
          title="Title"
          visible={visible}
          onOk={this.handleOk}
          confirmLoading={confirmLoading}
          onCancel={this.handleCancel}
        >
          <Booking/>
        </Modal>
      </div>
    );
  }
}

const { Column } = Table;

const BComponent = (props) => {
  return(
    <div>
      <MainLayout>
          <AddBooking/>
          <Table dataSource={props.data}>
          <Column title="Date" dataIndex="date" key="date" />
          <Column title="Time" dataIndex="time" key="time" />
          <Column title="Flexibility Before" dataIndex="FlexibilityBefore" key="FlexibilityBefore" />
          <Column title="Flexibility After" dataIndex="FlexibilityAfter" key="FlexibilityAfter" />
          <Column
          title="Action"
          key="action"
          render={(text, record) => (
              <span>
              <a >Edit</a>
              <Divider type="vertical" />
              <a>Delete</a>
              </span>
          )}
          />
        </Table>  
      </MainLayout>
    </div>
  );
}
export default BComponent;
