import React, { Component } from "react";
import {Link, withRouter} from 'react-router-dom';
import ReactDOM from 'react-dom';

import { Layout, Menu, Breadcrumb } from 'antd';
import 'antd/dist/antd.css';
import './main-layout.css';

const { Header, Content, Footer } = Layout;

const breadcrumbNameMap = {
  '/' : 'Home',
  '/login' : 'Login',
  '/register' : 'Register',
  '/home/booking' : 'Booking',  
}
const Home = withRouter(props => {
  const { location } = props;
  const pathSnippets = location.pathname.split('/').filter(i => i);
  const extraBreadcrumbItems = pathSnippets.map((_, index) => {
    const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
    return (
      <Breadcrumb.Item key={url}>
        <Link to={url}>{breadcrumbNameMap[url]}</Link>
      </Breadcrumb.Item>
    );
  });
  const breadcrumbItems = [
    <Breadcrumb.Item key="home">
      <Link to="/">Home</Link>
    </Breadcrumb.Item>,
  ].concat(extraBreadcrumbItems);
  return (
    <div className="crumb">
      <Breadcrumb>{breadcrumbItems}</Breadcrumb>
    </div>
  );
});

class MainLayout extends React.Component{
  render() {
    return (
      <div>
        <Layout className="layout">
        <Header>
          <div className="logo" />
          <Menu
            theme="dark"
            mode="horizontal"
            // defaultSelectedKeys={['2']}
            style={{ lineHeight: '64px' }}
          >
            <Menu.Item key="1"><a href="/home">Home</a></Menu.Item>
            <Menu.Item key="2"><a href="/home/login">Login</a></Menu.Item>
            <Menu.Item key="3"><a href="/home/register">Register</a></Menu.Item>
          </Menu>
        </Header>
        <Content style={{ padding: '0 50px' }}>
          <Home/>
          {/* <div style={{ background: '#fff', padding: 24, minHeight: 280 }}>Content</div> */}
          <div style={{ background: '#fff', padding: 24, minHeight: 280 }}>{this.props.children}</div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>Made by Zense</Footer>
      </Layout>
      </div>
    );
  }
}
export default MainLayout;
